const burger = document.querySelector(".burger"),
    menu = document.querySelector(".header_list");
burger.addEventListener("click", (function() { burger.classList.toggle("active"), menu.classList.toggle("active"), document.body.classList.toggle("lock") }));
const headerLinks = document.querySelectorAll(".header_link[data-goto]");
if (headerLinks.length > 0) {
    function onHeaderLinkClick(e) { const t = e.target; if (t.dataset.goto && document.querySelector(t.dataset.goto)) { const o = document.querySelector(t.dataset.goto).getBoundingClientRect().top + pageYOffset - document.querySelector("header").offsetHeight;
            window.scrollTo({ top: o, behavior: "smooth" }), e.preventDefault() } }
    headerLinks.forEach(e => { e.addEventListener("click", onHeaderLinkClick) }) }